import React from 'react'

export default function PhotoDisplay({showImage,setClickBiggerImg}) {
    const handleCloseButton = () => {
        setClickBiggerImg(false)
    }

    return (
        <div className="photo-display-wrapper">
            <button className="close-btn" ><img src="close.png" alt="close" className="close-icon"/></button>
            <img src={`data:image/png;base64,${showImage}`} alt="item-img-bigger" className="bigger-size"/>
        </div>
    )
}
