import React,{useCallback,useState,useRef,useEffect} from 'react'
import infinityScroll from './InfinityScroll'
import axios from 'axios'
import {useAuth0} from '@auth0/auth0-react'

// import Calendar from 'react-calendar'
// import 'react-calendar/dist/Calendar.css'

export default function PictureGallery({setClickPhoto,setClickGallery}) {
    const observer = useRef()
    const [pageNumber,setPageNumber] = useState(5)
    const [keyword,setKeyword] = useState('')
    const [selection,setSelection] = useState("location")
    const [itemLocation,setItemLocation] = useState("")
    const [createAt,setCreateAt] = useState("")
    const [updateAt,setUpdateAt] = useState("")
    const {isAuthenticated,getAccessTokenSilently} = useAuth0()

    // const [enableCalendar,setEnableCalendar] = useState(false)
    // const [value, setCalendar] = useState(new Date())
    // const [buttonType,setButtonType] = useState(false)
    // const [keydown,setKeyDown] = useState("")
    const [state,setState] = useState(false)
    
    const {
        inventory,
        hasMore,
        error,
        loading,change
    } = infinityScroll(pageNumber,keyword,selection)
    

    const lastInventory= useCallback((node) =>{
        if(loading) return
        if(observer.current) observer.current.disconnect()
        observer.current = new IntersectionObserver(entries =>{
            if(entries[0].isIntersecting && hasMore) setPageNumber(prevInventory => prevInventory + 5)
        })
        if(node) observer.current.observe(node)
    },[loading,hasMore])

    const handleItemSearch = ((e) =>{
        setKeyword(e.target.value)
    })

    const handleSelection = ((e) =>{
        setSelection(e.target.value)
    })

    useEffect(() =>{
        if (change){
            setKeyword("")
        }
    },[change])

    const handlePictureClick = async (id) =>{
        if(isAuthenticated){
            const token = await getAccessTokenSilently()
            await axios.get(`http://localhost:5000/inventory/${id}`,{
                headers:{
                    authorization: `Bearer ${token}`
                }
            })
            .then(res =>{
                setItemLocation(res.data.location)
                setCreateAt(res.data.createdAt)
                setUpdateAt(res.data.updatedAt)
            })
            .catch(err => console.log(err))
        }
    }

    // useEffect(()=>{
        
    //     switch(selection){
    //         case "date":
    //             setEnableCalendar(true)
    //             setKeyword(value.getFullYear()+'-' + (value.getMonth()+1) + '-'+value.getDate())
    //             setButtonType(true) 
    //             setState(false)
    //             break
    //         case "userId":
    //             setEnableCalendar(false)
    //             setButtonType(true)
    //             if (keyword !== undefined) {
    //                 setKeyword(localStorage.getItem('userId'))
    //             }
    //             setState(false)
    //             break
    //         default:
    //             if(keyword === localStorage.getItem('userId') || keyword === value.getFullYear()+'-' + (value.getMonth()+1) + '-'+value.getDate()) {
    //                 setKeyword("")
    //             }
    //             setEnableCalendar(false)
    //             setButtonType(false)
    //             setState(true)
    //             break
    //     }
    // },[selection,value,keyword])

    // useEffect(() =>{
    //     if (keydown !== "" && keyword === ""){
    //         setPageNumber(5)
    //     }
    // },[keydown,keyword])

    // const handleKeyDown = ((e) =>{
    //     setKeyDown(e.target.value)
    // })

    return ( 
        <div className="gallery-wrapper">
            <button className="close-btn" onClick={() => setClickGallery(false)}><img src="close.png" alt="close" className="close-icon"/></button>
            <div className="picture-slider">
                {inventory.map((item,index)=>{
                    if(inventory.length === index +1){
                            return <img key={item.photo} ref={lastInventory} src={`data:image/png;base64,${item.photo}`} alt="sideBarPhoto" className="img-slider" onClick={() => handlePictureClick(item._id)}/>
                    }
                    else{
                        return <img key={item.photo} src={`data:image/png;base64,${item.photo}`} alt="sideBarPhoto" className="img-slider" onClick={() => handlePictureClick(item._id)}/> 
                    }
                })}
                {state ? <div>{loading && 'Loading...'}</div> : ""}
                <div>{error && 'Error'}</div>
            </div>
            <div className="picture-info">
                <div className="item-info-wrapper">
                    <div className="gallery-btn">
                        <button className="delete-btn">Delete</button>
                        <button className="edit-btn">Edit</button>
                        <button className="save-btn">Save</button>
                    </div> <br />
                    <div className="item-info">
                        <p>Item location: {itemLocation}</p>
                        <p>Created At: {createAt}</p>
                        <p>Updated At: {updateAt}</p>
                    </div>
                </div>
                <div className="item-picture">item picture</div>
            </div>
        </div>
        // <div className="grid-item sidebar-wrapper">
        //     <div className="search-item-bar">
        //         {enableCalendar ?
        //          <Calendar
        //             className="calendar"
        //             onChange={setCalendar}
        //             value={value}
        //             next2Label=""
        //             prev2Label=""
        //         /> : 
        //         <input
        //             type={buttonType ? "hidden": "search"}
        //             placeholder="Search existing item"
        //             className="search-item"
        //             onChange={handleItemSearch}
        //             value ={keyword}
        //             disabled={buttonType ? "disabled": ""}
        //             onKeyDown={handleKeyDown}
        //         />}
        //     <p className="sort-title">Sort by:</p>
        //     <select name="sort" className="sort-select" onChange={handleSelection} value={selection}>
        //         <option value="location" defaultValue>Location</option>
        //         <option value="userId">Create By You</option>
        //         <option value="date">Date</option>
        //     </select>
            // </div>
            
        // </div>
    )
}
