import React,{useEffect,useState} from 'react'
// import {io} from 'socket.io-client'
import axios from 'axios'
import {useAuth0} from '@auth0/auth0-react'

export default function SearchBar({setDeviceCheck,deviceCheck,setButtonSubmit,buttonSubmit}) {
    const [socket,setSocket] = useState()
    const [keyword,setKeyword] = useState("")
    const [userId,setUserId] = useState("")
    const [checked,setChecked] = useState(false)
    const heroku = 'https://schaeffler.herokuapp.com/'

    const {isAuthenticated,getAccessTokenSilently} = useAuth0()

    // 'http://localhost:5000/'
    // useEffect(() => {
    //     const s = io('http://localhost:5000/') //change here
    //     // const s = io(heroku) //change here
    //     setSocket(s)
    //     return () =>{
    //         s.disconnect()
    //     }
    // }, []) 

    const onSearchClickButton = () => {
        if(!checked || !deviceCheck){
            alert('Please make sure to check the device and the rack placement.')
        }
        else {
            setButtonSubmit(true)
            setDeviceCheck(false)
            setChecked(false)
        }
    }

    const onSearching = (event) =>setKeyword(event.target.value)

    useEffect(() =>{
        async function getAccessToken(){
            if(isAuthenticated){
                const token = await getAccessTokenSilently()
                await axios.get('http://localhost:5000/protected',{ //change here
                // await axios.get(heroku+'protected',{ //change here
                    headers: {
                        authorization:`Bearer ${token}`
                    }
                })
                .then(response => {
                    setUserId(response.data.sub)
                })
                .catch(err => console.log(err))
            }
        }
        getAccessToken()  
    },[getAccessTokenSilently,isAuthenticated])

    useEffect(() =>{
        async function getAccessToken(key){
            if(isAuthenticated){
                const token = await getAccessTokenSilently()
                console.log(token)
                await axios({
                    method:'POST',
                    url: 'http://localhost:5000/search', //change here
                    // url:  heroku+'search', //change here
                    headers: {
                        'Content-Type': 'application/json',
                        authorization:`Bearer ${token}`
                    },
                    data:key
                })
                .then(response => {
                    console.log(response)
                })
                .catch(err => console.log(err))
            }
        }

        var regexp = /^\d{1,2}\.\d{1,2}\.\d{1,2}?$/
        if(buttonSubmit === true){
            if (keyword !== "" && regexp.test(keyword)){
                const key = {
                    "keyword":keyword,
                    "userId":userId
                }
                // socket.emit("begin-search",key)
                getAccessToken(key)
                setButtonSubmit(false)
                setKeyword("")
            }
            else if(!regexp.test(keyword)){
                alert("Invalid keyword")
            }
            else{
                alert("Please enter a keyword")
            } 
        }
    },[keyword,buttonSubmit,userId,socket,setButtonSubmit,getAccessTokenSilently,isAuthenticated])

    return (
        <>
            <p className="title-text">SEARCH TOOL</p>
            <input
                type="search"
                placeholder="Search inventory"
                className="search-box"
                onChange={onSearching}
                value={keyword}
            />
            <img src="search.png" alt="manifying-glass" className="search-btn"/>
            <div className="radio-btn-wrapper">
                <input type="radio" value={checked} onChange={() => setChecked(true)} checked={checked}/>
                <label htmlFor="agreement" className="label-agreement">Placed at rack {keyword.substring(0,keyword.indexOf('.'))}</label>
            </div>
            <button type="submit" className="search-btn" onClick={onSearchClickButton}>SEARCH</button>
        </>
       
    )
}
