import React,{useEffect,useState} from 'react'
import {io} from 'socket.io-client'
import popUp from './PopUp'

export default function SearchBar({userId}) {
  
    const [socket,setSocket] = useState()
    const [pressed,setPress] = useState(false)
    const [keyword,setKeyword] = useState()
    const [buttonClicked,setButtonClicked] = useState(false)
    const [enablePopUp,setEnablePopUp] = useState(false)
    const [cancelBtn,setCancelBtn] = useState(false)
     // const [clicked,setClicked] = useState(false)
    const heroku = 'https://schaeffler.herokuapp.com/'
    const {
        queue,
        error,
    } = popUp()

    // 'http://localhost:5000/'
    useEffect(() => {
        const s = io('http://localhost:5000/')
        setSocket(s)
        return () =>{
            s.disconnect()  
        }
    }, []) 

    const onPress = (event) =>
    {
        if(event.key === 'Enter'){
            setPress(true)
        }
    }

    const onSearchClickButton = () =>
    {
        // setClicked(true)
        setButtonClicked(true)
    }

    const onSearching = (event) =>{
        setKeyword(event.target.value);
    }

    useEffect(() =>{
        if (socket === null || keyword === null) return

        if((pressed === true ) || (buttonClicked === true)){
            if (keyword !== ""){
                const key = {
                    "keyword":keyword,
                    "userId":userId,
                    "socketId":socket.id
                }
                socket.emit("begin-search",key)
                setPress(false)
                setButtonClicked(false)
                setKeyword("")
            }
            else{
                alert("Please enter a keyword")
            } 
        }
    },[socket,keyword,pressed,userId,buttonClicked])

    // const onInputClick = (()=>{
    //     setClicked(true)
    // })
    
    useEffect(() =>{
        if(socket == null) return
        const handler = (delta) =>{
            setEnablePopUp(delta)
        }
        socket.on('popup',handler)

        return () =>{
            socket.off('popup',handler)
        }
    },[socket,enablePopUp])

    const handleCancelButton = (()=>{
        setCancelBtn(true)
    })



    return (
        <>
            <span >
                <input
                    type="search"
                    placeholder="Search inventory"
                    className="search-box"
                    onChange={onSearching}
                    value={keyword}
                    onKeyPress={onPress}
                    // onClick={onInputClick}
                    // style = {clicked ? {width:450} : {width:300}}
                />
                <button type="button" className="search-btn" onClick={onSearchClickButton}>          
                    <img src="search.png" alt="manifying-glass" />
                </button>
            </span>

            {enablePopUp ?  <div className={cancelBtn ? "popup-hidden": "popup-container"}>
                {/* <div classsName="cancel-btn"> */}
                    <button className="cancel-button" onClick={handleCancelButton}>X</button>     
                {/* </div> */}
                {queue.map((item,index)=>{
                // if(inventory.length === index +1){
                //     return <div className="inventory-item">
                //         <div key={item.location} ref={lastInventory}>Location: {item.location}</div>
                //         <div>Create At: {item.createdAt}</div>
                //         <img key={item.photo} ref={lastInventory} src={item.photo} alt="sideBarPhoto" onClick={() => setClickPhoto(item.photo)} className="img-sideBar"/>
                //     </div>
                // }
                // else{
                    return <div className="inventory-item">
                        <div key={item.location}>Location: {item.location}</div>
                    </div>
                // }
            })}
                
            </div> : ""}

        </>
       
    )
}
