import React,{useState,useEffect} from 'react'
import SearchBar from './SearchBar'
import Status from './Status'
import Pusher from 'pusher-js'
import ReactNotification from 'react-notifications-component'
import {store} from 'react-notifications-component'
import 'react-notifications-component/dist/theme.css'

export default function ControlBox() {
    const [deviceCheck,setDeviceCheck] = useState(false)
    const [carVelocity,setCarVelocity] = useState(0)
    const [carLocation,setCarLocation] = useState("undefined")
    const [buttonSubmit,setButtonSubmit] = useState(false)

    const [droneBattery,setDroneBattery] = useState(0)
    const [droneAltitude,setDroneAltitude] = useState(0)
    const [droneLocation,setDroneLocation] = useState("undefined")

    const [droneConnection,setDroneConnection] = useState(false)
    const [carConnection,setCarConnection] = useState(false)

    useEffect(() =>{
        const pusher = new Pusher(process.env.REACT_APP_PUSHER_KEY,{
            'cluster':process.env.REACT_APP_PUSHER_CLUSTER,
            forceTLS: true,
        })

        const messageChannel  = pusher.subscribe('droneMessage')
        messageChannel.bind('send',function(data){
            setDroneBattery(data.battery)
            setDroneAltitude(data.altitude)
            setDroneConnection(true)
            setDroneLocation(data.location)
        })
        return () => {
            pusher.unsubscribe(messageChannel)
            pusher.disconnect()
        }
    },[])

    useEffect(() =>{
        const pusher = new Pusher(process.env.REACT_APP_PUSHER_KEY,{
            'cluster':process.env.REACT_APP_PUSHER_CLUSTER,
            forceTLS: true,
        })

        const messageChannel  = pusher.subscribe('carMessage')
        messageChannel.bind('send',function(data){
            setCarVelocity(data.velocity)
            setCarLocation(data.location)
            setCarConnection(true)
        })

        messageChannel.bind('connection',(status)=>{
            setCarConnection(status)
            setCarVelocity(0)
            setCarLocation(undefined)
        })

        return () => {
            pusher.unsubscribe(messageChannel)
            pusher.disconnect()
        }
    },[])

    useEffect(() =>{
        if(buttonSubmit){
            store.addNotification({
                title:"New search",
                message:"User something has searched something",
                type: "success",
                container:"bottom-right",
                insert:"bottom",
                // animationIn:["animated","fadeIn"],
                // animationOut:["animated","fadeOut"],
                dismiss: {
                    duration: 2000,
                    onScreen: true
                },
                width:400
            })
            setButtonSubmit(false)
        }
        else if(droneConnection && carConnection){
            store.addNotification({
                title:"Device status",
                message:"Car and Drone are online",
                type: "success",
                container:"bottom-right",
                insert:"bottom",
                animationIn:["animated","fadeIn"],
                animationOut:["animated","fadeOut"],
                dismiss: {
                    duration: 2000,
                    onScreen: true
                },
                width:400
            })
        }
    },[buttonSubmit,droneConnection,carConnection])

    return (
        <>
            <div className="wrapper">
                <div className="search-sidebar">
                    <SearchBar setDeviceCheck={setDeviceCheck} deviceCheck={deviceCheck} setButtonSubmit={setButtonSubmit} buttonSubmit={buttonSubmit}/>
                </div>
                <div className="status-wrapper">
                    <Status setDeviceCheck={setDeviceCheck} deviceCheck={deviceCheck} carLocation={carLocation} carVelocity={carVelocity} droneBattery={droneBattery} droneAltitude={droneAltitude} droneLocation={droneLocation}/>
                </div>
                <ReactNotification/>
            </div>
        </>
    )
}
