import Homepage from './Homepage'
import HandleLogin from './HandleLogin'
import "bootstrap/dist/css/bootstrap.min.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from 'react-router-dom'
import React,{useEffect,useState} from 'react'
import {useAuth0} from '@auth0/auth0-react'
import PublicRoute from './Route/PublicRoute'
import PrivateRoute from './Route/PrivateRoute'

function App() {
  const {isAuthenticated} = useAuth0()
  const [data,setData] = useState(isAuthenticated)

  useEffect(() =>{setData(isAuthenticated)},[])

  return (
  <Router>
      <Switch>
        <PublicRoute restricted={data} component={HandleLogin} path="/" exact/>
        <PrivateRoute restricted={data} component={Homepage} path="/homepage" exact />
      </Switch>
    </Router>
  )
}

export default App;
