import React,{useRef,useEffect} from 'react'

export default function Mapping() {

      const canvas = useRef()
      const imgRef = useRef()
      const canvasWidth = 1000
      const canvasHeight = 900
      const racks = 9
      const rows = 16
      let cellSide = 20
    
      function zeros(dimensions) {
        var array = []
    
        for (var i = 0; i < dimensions[0]; ++i) {
            array.push(dimensions.length == 1 ? 0 : zeros(dimensions.slice(1)));
        }
    
        return array
    }
    //   useEffect(() => {
        // context.save()
        // context.scale(pixelRatio, pixelRatio)
        // context.fillStyle = "hsl(0, 0%, 95%)"
        // context.fillRect(0, 0, width, height)
    
        // context.strokeStyle = "black"
        // context.beginPath()
        // context.arc(width / 2, height / 2, width / 4, 0, Math.PI * 2)
        // context.stroke()
        // context.restore()
    //   },[])
    
    //   const dw = Math.floor(pixelRatio * width)
    //   const dh = Math.floor(pixelRatio * height)
    //   const style = { width, height }
    // const array2D = [
    //     ['R', 'Y', 'R'],
    //     ['Y', 'R', 'Y'],
    //     ['R', 'Y', 'R']
    // ]

    const draw = (context) => {
        context.drawImage(imgRef.current, 10, 10,imgRef.current.width, imgRef.current.height)
        for (let i = 0; i < zeros([racks,rows]).length; i++) {    
            for (let j = 0; j < zeros([racks,rows])[i].length; j++) {
                let x = j * 55 + 38
                let y = i * 20 + 55
                let count = 110

                let cellColor = 'rgba(255, 0, 0, 0)'    

                if (i % 2 === 0 && j % 2 === 0) {
                    cellColor = '#f1c40f'
                }
               
                if (i !== 0 && i !== 1){
                    y = i * 20 + count
                    count += 60
                    if(i !== 2 && i !== 3){
                        y = i * 20 + count
                        count += 60
                        if (i !== 4 && i !== 5){
                            y = i * 20 + count
                            count += 60
                            if (i === 8){
                                y = i * 20 + count
                            }
                        }
                    }
                }
                context.beginPath()
                context.fillStyle = cellColor
                context.fillRect(x, y, cellSide, cellSide)
            }
        }
    }

    useEffect(()=>{
        const context = canvas.current.getContext("2d")
        draw(context)
    },[draw])
    
    const handleOnLoad = () => {
        
    }

    return (
        <div className="mapping">
            <canvas ref={canvas} width={canvasWidth} height={canvasHeight} />
            <img ref={imgRef} src="mapping.png" alt="mapping" className="mapping-img" onLoad={handleOnLoad}/>
        </div>        
    )
}
