import React,{useEffect,useState} from 'react'
import {io} from 'socket.io-client'
import ReactSpeedometer from "react-d3-speedometer"

export default function Status() {
    const [socket,setSocket] = useState()
    const [velocity,setVelocity] = useState(0)
    const [battery,setBattery] = useState(0)
    const heroku = 'https://schaeffler.herokuapp.com/'

    useEffect(() => {
        const s = io('http://localhost:5000/')
        setSocket(s)
        return () =>{
            s.disconnect()  
        }
    }, [])  

    useEffect(() =>{
        if(socket == null) return

        const handler = (delta) =>{
            setVelocity(delta['velocity'])
            setBattery(delta['battery percentage'])
        }
        socket.on('receive-raspberry',handler)
        return () =>{
            socket.off('receive-raspberry',handler)
        }
    },[socket,velocity,battery])

    return (
        <div className="status-wrapper">
            {/* <div>Velocity : {velocity} m/s</div> */}
            <div>Battery : {battery} %</div>
            <ReactSpeedometer
            width={230}
            maxValue={50}
            value={velocity}
            needleColor="red"
            startColor="green"
            segments={5}
            endColor="blue"
            />
        </div>
    )
}
