import React,{useState,useEffect} from 'react'
import Mapping from './Mapping'
import ControlBox from './ControlBox'
import PictureGallery from './PictureGallery'
import Queue from './Components/Queue'
import ReactNotification from 'react-notifications-component'
import {store} from 'react-notifications-component'
import 'react-notifications-component/dist/theme.css'
import Pusher from 'pusher-js'

export default function RightBody({setClickGallery,clickGallery,setQueueOnClick,queueOnClick}) {
    const [buttonSubmit,setButtonSubmit] = useState(false)
    const [droneConnection,setDroneConnection] = useState(false)
    const [carConnection,setCarConnection] = useState(false)
    const [available,setAvailable] = useState(true)

    useEffect(() =>{
        const pusher = new Pusher(process.env.REACT_APP_PUSHER_KEY,{
            'cluster':process.env.REACT_APP_PUSHER_CLUSTER,
            forceTLS: true,
        })

        const messageChannel  = pusher.subscribe('droneMessage')
        messageChannel.bind('send',function(data){
            setDroneConnection(JSON.parse(data.droneConnection))
        })
        return () => {
            pusher.unsubscribe(messageChannel)
            pusher.disconnect()
        }
    },[])

    useEffect(() =>{
        const pusher = new Pusher(process.env.REACT_APP_PUSHER_KEY,{
            'cluster':process.env.REACT_APP_PUSHER_CLUSTER,
            forceTLS: true,
        })

        const messageChannel  = pusher.subscribe('carMessage')
        messageChannel.bind('send',function(data){
            setCarConnection(JSON.parse(data.carConnection))
        })

        return () => {
            pusher.unsubscribe(messageChannel)
            pusher.disconnect()
        }
    },[])

    function getNotification(title,message,messageType,duration,width){
        store.addNotification({
            title:title,
            message:message,
            type: messageType,
            container:"bottom-right",
            insert:"bottom",
            animationIn:["animated","fadeIn"],
            animationOut:["animated","fadeOut"],
            dismiss: {
                duration: duration,
                onScreen: true
            },
            width:width
        })              
    }

    useEffect(()=>{
        if(buttonSubmit === true && carConnection === false && !droneConnection === false){
            getNotification("Warning","Device is not online but your search will be saved.","warning",5000,500)
            setQueueOnClick(true)
        }
        if(droneConnection && carConnection){
            getNotification("Device Status","Car and Drone are online","success",5000,400)       
        }
        if(available === false){
            getNotification("Device Status","Process are being executed. Try again later","danger",5000,400)       
        }
        else if(available === true){
            getNotification("Device Status","Search can be performed now.","success",5000,400)       
        }
    },[buttonSubmit,carConnection,droneConnection,setQueueOnClick,available])

    return (
        <>
            <ControlBox buttonSubmit={buttonSubmit} setButtonSubmit={setButtonSubmit} droneConnection={droneConnection} carConnection={carConnection}/>
            <Mapping/>
            {clickGallery ? <PictureGallery setClickGallery={setClickGallery}/>: ""}
            {queueOnClick ? <Queue setQueueOnClick={setQueueOnClick} setAvailable={setAvailable}/>: ""}
            <ReactNotification/>
        </>
    )
}
