import React,{useEffect,useState} from 'react'
// import {io} from 'socket.io-client'
import infinityScroll from './InfinityScroll'

export default function SidebarBox({setClickPhoto}) {
    // const [location,setLocation] = useState()
    // const [photo,setPhoto] = useState()
    // const [socket,setSocket] = useState()
    const [pageNumber,setPageNumber] = useState(10)
    const [y,setY] = useState(window.scrollY)
    const {
        inventory,
        hasMore,
        error,
        loading,
        end,
        dataLength
    } = infinityScroll(pageNumber)

    // useEffect(() => {
    //     const s = io(`http://localhost:5000`)
    //     setSocket(s)
    //     return () =>{
    //         s.disconnect()  
    //     }
    // }, [])  

    // useEffect(() =>{
    //     if (socket == null) return
    //     const handler = (delta) =>{
    //         setLocation(delta['location'])
    //         setPhoto(delta['photo'])
    //     }
    //     socket.on('display-result',handler)
    //     return () =>{
    //         socket.off('display-result',handler)
    //     }
    // },[socket,photo,location])

    useEffect(()=>{
        console.log(end)
        if(end === true){
            window.removeEventListener("scroll", setPageNumber(dataLength))
        }
        window.addEventListener("scroll", setPageNumber(pageNumber))
        return () => { // return a cleanup function to unregister our function since its gonna run multiple times
            window.removeEventListener("scroll", setPageNumber(dataLength))
          }
    },[y,pageNumber,end,dataLength])

    return (
        <div className="grid-item sidebar-wrapper">
            {inventory.map(item=>{
                return <div className="inventory-item">
                    <div key={item.location}>Location: {item.location}</div>
                    <img key={item.photo} src={item.photo} alt="sideBarPhoto" onClick={() => setClickPhoto(item.photo)} className="img-sideBar"/>

                </div>
            })}
            <div>{loading && 'Loading...'}</div>
            <div>{error && 'Error'}</div>
        </div>
    )
}
