import React from 'react'

export default function Battery() {
    // const [level, setLevel] = useState(v)
    const [color, setColor] = useState("green")

    useEffect(()=>{
        if(value<=30){
            setColor("red")
        }else if(value<=70){
            setColor("orange")
        }else setColor("green")
    },[value])
    
      return(
        <div className="d-flex align-items-center pr-4 pl-2">
            <div className="fa-battery-filling"><span className={color} style={{width:`calc(${value}% * 0.95)`}}></span></div>
            <div className="battery-head"></div>
            <h1 className="battery-level">{value}%</h1>
        </div>
        
      )
}
