import React from 'react'

export default function PhotoDisplay({clickPhoto,setOnClick,onClick}) {
    const photoDisplayOnClick = (()=>{
        setOnClick(true)
    })
    return (
        <div className="photo-display-wrapper" onClick={photoDisplayOnClick}>
            <button className="close-btn" onClick={handleCloseButton}><img src="close.png" alt="close" className="close-icon"/></button>
            <img src={`data:image/png;base64,${clickPhoto}`} alt="item-img-bigger" className="bigger-size"/>
        </div>
    )
}
