import React,{useRef,useEffect,useState} from 'react'

export default function Mapping() {

      const canvas = useRef()
      const imgRef = useRef()
      const droneRef = useRef()
      const canvasWidth = 1000
      const canvasHeight = 900
      const cellSide = 20
    //   const droneX = 900
    //   const droneY = 500
      const droneX = 900
      const droneY = 500
    
      const [rowDes,setRowDes] = useState(0)
      const [rackDes,setRackDes] = useState(5)
      const margin = 90

    const draw = (context) => {
        context.drawImage(imgRef.current, 10, 10,imgRef.current.width, imgRef.current.height)
    }

    // const drawItem = (context) =>{
    //     const desX = rowDes * 55 + 38
    //     rackDes = rackDes - 1 
    //     // if(rackDes === 0 || rackDes === 1){
    //         droneY = rackDes * cellSide + 220
    //         console.log(droneY)
    //     // }
    //     // else{
    //     //     droneY = rackDes * cellSide + Math.trunc(rackDes/2) * margin
    //     // }
    //     if(desX === droneX){
    //         droneX = desX
    //     }
    //     while (desX < droneX){
    //         droneX -= 1
    //         context.beginPath()
    //         context.drawImage(droneRef.current,droneX,droneY,droneRef.current.width,droneRef.current.height)
    //     }

    // }
    
    const handleMapOnLoad = () => {
        const context = canvas.current.getContext("2d")
        draw(context)
    }

    const handleItemOnLoad = () => {
        const context = canvas.current.getContext("2d")
        const desX = rowDes * 55 + 38
        const desY = rackDes * cellSide + 220
        setRackDes(rackDes -1)
        if(context){
            if(rowDes === 0){
                context.beginPath()
                context.drawImage(droneRef.current,droneX,droneY,droneRef.current.width,droneRef.current.height)
            }
            else{
                let tmpX = 863
                while(desX < tmpX){
                    tmpX -= 1
                    context.beginPath()
                    context.drawImage(droneRef.current,tmpX,desY,droneRef.current.width,droneRef.current.height)
                }
            }
            
        }
    }

    // const handleItemOnLoad = () =>{
    //     const context = canvas.current.getContext("2d")
    //     drawItem(context)
    // }

    // while (desX < droneX){
    //     droneX -= 1
    // }
  
    // useEffect(()=>{
    //     const context = canvas.current.getContext("2d")
        
        
    // },[rackDes,rowDes])
    // img.onload = () =>{
    //     context.drawImage(imgRef.current, 10, 10,imgRef.current.width, imgRef.current.height)
    // }

    return (
        <div className="mapping">
            <canvas ref={canvas} width={canvasWidth} height={canvasHeight} />
            <img ref={imgRef} src="mapping.png" alt="mapping" className="mapping-img" onLoad={handleMapOnLoad}/>
            <img ref={droneRef} src="drone.png" alt="drone" className="drone-piece" onLoad={handleItemOnLoad}/>
        </div>        
    )
}
