import React,{useState,useEffect} from 'react'
import LogoutButton from './Components/LogoutButton'
import Upload from './Components/Upload'
import {useAuth0} from '@auth0/auth0-react'
import axios from 'axios'

export default function Setting({setQueueOnClick}) {
    const [userPicture,setUserPicture] = useState("")
    const {isAuthenticated,getAccessTokenSilently} = useAuth0()

    useEffect(() =>{
        async function getAccessToken(){
            if(isAuthenticated){
                const token = await getAccessTokenSilently()
                await axios.get('http://localhost:5000/protected',{
                    headers: {
                        authorization:`Bearer ${token}`
                    }
                })
                .then(response => {
                    setUserPicture(response.data.picture)
                })
                .catch(err => console.log(err))
            }
        }
        getAccessToken()  
    },[getAccessTokenSilently,isAuthenticated])

    return (
        <span className="setting-wrapper">
            {/* <img src="upload.png" alt="noti" className="img-wrapper"/> */}
            <div className="dropdown">
                <img src={userPicture} alt="user" className="img-wrapper"/>
                <div className="dropdown-content">
                    <LogoutButton/>
                    <Upload/>
                    <img src="queue.png" alt="queue" className="img-wrapper" onClick={() => setQueueOnClick(true)}/>
                </div>
            </div>
        </span>
    )
}
