    return (
        <span className="search-bar">
        <form>
            <input
                type="search"
                placeholder="Search inventory"
                className="search-box"
                onChange={onSearching}
                value={keyword}
                onKeyPress={onPress}
            />
            {/* <button type="button" className="search-btn" onClick={onSearchClickButton}>Search</button> */}
        </form>
        </span>
    )
}
