import React,{useState,useEffect} from 'react'
import './stylesheet/sidebar.css'
import { useAuth0 } from '@auth0/auth0-react'
import Mapping from './Mapping'

export default function Sidebar({navBarState}) {
    const {isAuthenticated,logout} = useAuth0()

    const data = [
        { className:"bx-grid-alt",value:"Dashboard"},
        { className:"bx-user",value:"Profile"},
        { className:"bx-folder",value:"Picture Gallery"},
        {className:"bx-layer",value:"Queue"},
        { className:"bx-bar-chart-alt-2",value:"Statistic"}
    ]

    return (
        <>
            <body className={navBarState ? "body-pd": ""} id="body-pd">
                
                <div className={navBarState ? "show l-navbar":"l-navbar"} id="nav-bar" >
                    <nav className="nav">
                        <div> 
                            <a className="nav_logo"> 
                                {/* <i className='bx bx-layer nav_logo-icon'></i>  */}
                                <img src="https://image.flaticon.com/icons/png/512/2876/2876741.png" alt="drone" className="drone-icon"/>
                                <span className="nav_logo-name">Inventory Check</span> 
                            </a>
                            <div className="nav_list"> 
                            { 
                                data.map((item,index) =>{
                                    if(index === 0){
                                        return <a href={window.location.origin} className="nav_link active">
                                            <i className={'bx nav_icon '+item.className}></i>
                                            <span className="nav_name">{item.value}</span>
                                        </a> 
                                    }
                                    else{
                                        return <a href="#" className="nav_link">
                                            <i className={'bx nav_icon '+item.className}></i>
                                            <span className="nav_name">{item.value}</span>
                                        </a> 
                                    }
                                   
                                })
                            }
                            </div>
                        </div> 
                        {isAuthenticated ? 
                        <a href={window.location.origin} className="nav_link" onClick={() => logout()}> 
                            <i className='bx bx-log-out nav_icon'></i> 
                            <span className="nav_name">Sign Out</span> 
                        </a> : ""}
                    </nav>
                </div>
                <Mapping/>
            </body>
        </>
    )
}
