import {useState,useEffect} from 'react'
import axios from 'axios'
import Pusher from 'pusher-js'
import path from 'path'
require('dotenv').config({path:'C:/Users/Admin/Downloads/capstone/schaeffler\.env'})

export default function PopUp() {

    const [queue,setQueue] = useState([])
    const [error,setError] = useState(false)
    const heroku = 'https://schaeffler.herokuapp.com/queue'
    // 'http://localhost:5000/queue'
    function getQueue(){
        console.log(process.env.REACT_APP_PUSHER_KEY)
        setError(false)
        axios({
            method:'GET',
            url: 'http://localhost:5000/queue',
        })
        .then(res => {
            setQueue(prevInventory =>{
                return [...new Set([...prevInventory,...res.data.map(i => i)])]
            })
        })
        .catch(e =>{
            setError(true)
            console.log('Error: '+e)
        })
    }
    useEffect(() => getQueue(),[])

    function fetchChanges(){
        setError(false)
            axios({
                method:'GET',
                url: heroku,
            })
            .then(res => {
                setQueue(() =>{
                    return [...new Set([...'',...res.data.map(i => i)])]
                })
            })
            .catch(e =>{
                setError(true)
                console.log('Error: '+e)
            })
    }

    useEffect(() =>{
        const pusher = new Pusher(process.env.REACT_APP_PUSHER_KEY,{
            'cluster':process.env.REACT_APP_PUSHER_CLUSTER,
            encrypted:true
        })
        const channel = pusher.subscribe('tasks')
        channel.bind('deleted',function(){
            fetchChanges()
        })
        channel.bind('inserted',function(){
            fetchChanges()
        })
        return () => channel.unbind()
    },[])

    return {queue,error}
}
