import React,{useState,useEffect} from 'react'
import './stylesheet/sidebar.css'
import { useAuth0 } from '@auth0/auth0-react'
import axios from 'axios'

export default function Sidebar() {
    const [navBarState,setNavBarState] = useState(false)
    const {isAuthenticated,getAccessTokenSilently,logout} = useAuth0()
    const [userPicture,setUserPicture] = useState("")

    const data = [
        { className:"bx-grid-alt",value:"Dashboard"},
        { className:"bx-user",value:"Users"},
        { className:"bx-folder",value:"Picture Gallery"},
        { className:"bx-bar-chart-alt-2",value:"Stats"}
    ]

    useEffect(() =>{
        async function getAccessToken(){
            if(isAuthenticated){
                const token = await getAccessTokenSilently()
                await axios.get('http://localhost:5000/protected',{//change here
                // await axios.get(heroku+'protected',{//change here
                    headers: {
                        authorization:`Bearer ${token}`
                    }
                })
                .then(response => {
                    setUserPicture(response.data.picture)
                })
                .catch(err => console.log(err))
            }
        }
        getAccessToken()  
    },[getAccessTokenSilently,isAuthenticated])

    return (
        <>
            <body className={navBarState ? "body-pd": ""} id="body-pd">
                <header className={navBarState? "body-pd header":"header"} id="header" >
                    <div className="header_toggle">
                         <i className={navBarState ? "bx-x bx bx-menu":'bx bx-menu'} id="header-toggle" value={navBarState} onClick={()=> { setNavBarState(!navBarState)}}></i> 
                    </div>
                    <div className="header_img"> 
                        <img src={userPicture} alt="user"/> 
                    </div>
                </header>
                <div className={navBarState ? "show l-navbar":"l-navbar"} id="nav-bar" >
                    <nav className="nav">
                        <div> 
                                <i class='bx bx-layer nav_logo-icon'></i> 
                                <span className="nav_logo-name">BBBootstrap</span> 
                            
                            <div className="nav_list"> 
                            { 
                                data.map((item,index) =>{
                                    <div className="nav_name">{item.value}</div>
                                    if(index === 0){
                                        <a href="#" className="nav_link active">
                                            <i className={'bx nav_icon '+item.className[0]}></i>
                                            <span className="nav_name">{item.value[0]}</span>
                                        </a> 
                                    }
                                    else{
                                        <a href="#" className="nav_link">
                                            <i className={'bx nav_icon '+item.className}></i>
                                            <span className="nav_name">{item.value}</span>
                                        </a> 
                                    }
                                   
                                })
                            }
                               
                                {/* <a href="#" className="nav_link"> 
                                    <i className='bx bx-user nav_icon'></i> 
                                    <span className="nav_name">Users</span> 
                                </a> 
                                <a href="#" className="nav_link"> 
                                    <i className='bx bx-folder nav_icon'></i> 
                                    <span className="nav_name">Picture Gallery</span> 
                                </a> 
                                <a href="#" className="nav_link"> 
                                    <i className='bx bx-bar-chart-alt-2 nav_icon'></i> 
                                    <span className="nav_name">Stats</span> 
                                </a>  */}
                            </div>
                        </div> 
                        {isAuthenticated ? 
                        <a href={window.location.origin} className="nav_link" onClick={() => logout()}> 
                            <i className='bx bx-log-out nav_icon'></i> 
                            <span className="nav_name">Sign Out</span> 
                        </a> : ""}
                    </nav>
                </div>
                <div className="height-100 bg-light">
                    <h4>Main Components</h4>
                </div>
            </body>
        </>
    )
}
