import React,{useCallback,useRef} from 'react'
import popUp from '../PopUp'

export default function Queue({setQueueOnClick}) {
    const observer = useRef()

    const handleCloseButton = () => {
        setQueueOnClick(false)
    }

    const {
        queue,
        error,
        loading,hasMore
    } = popUp()

    const lastItem= useCallback((node) =>{
        if(loading) return
        if(observer.current) observer.current.disconnect()
        observer.current = new IntersectionObserver(entries =>{
            if(entries[0].isIntersecting && hasMore) setPageNumber(prevInventory => prevInventory + 5)
        })
        if(node) observer.current.observe(node)
    },[loading,hasMore])

    return (
        <div className="queue-wrapper">
            <button className="close-btn" onClick={handleCloseButton}><img src="close.png" alt="close" className="close-icon"/></button>
            <div className="queue-slider">
            {queue.map((item,index)=>{
                        if(item.length === index +1){
                            return <div className="queue-item">
                                <span key={item.keyword} ref={lastItem} >Search location: {item.keyword}</span>
                                <button type="button" className="cancel-item-btn" value={item._id} ref={lastItem}>cancel</button>
                                <button type="button" className="execute-item-btn" value={item._id} ref={lastItem}>execute</button>
                            </div>
                        }
                        else{
                            return <div className="queue-item" key={(index).toString()}>
                                <span key={(index+1).toString()}>Search location: {item.keyword}</span>
                                <button key={(index+2).toString()} type="button" className="cancel-item-btn" value={item._id}>cancel</button>
                                <button key={(index+3).toString()} type="button" className="execute-item-btn" value={item._id}>execute</button>
                            </div>
                        }
                    })}
                    <div>{error && 'Error'}</div>
            </div>
        </div>
    )
}
