import React,{useState,useEffect} from 'react'
import SearchBar from './SearchBar'
import Status from './Status'

export default function ControlBox() {
    const [deviceCheck,setDeviceCheck] = useState(false)
    const [carVelocity,setCarVelocity] = useState(0)
    const [carLocation,setCLocation] = useState("undefined")
    const [connection,setConnection] = useState(false)

    useEffect(() =>{
        const pusher = new Pusher(process.env.REACT_APP_PUSHER_KEY,{
            'cluster':process.env.REACT_APP_PUSHER_CLUSTER,
            forceTLS: true,
        })

        const messageChannel  = pusher.subscribe('carMessage')
        messageChannel.bind('send',function(data){
            setVelocity(data.velocity)
            setLocation(data.location)
            setConnection(true)
        })


        messageChannel.bind('connection',(status)=>{
            setConnection(status)
            setVelocity(0)
            setLocation(undefined)
            console.log(status)
        })

        return () => {
            pusher.unsubscribe(messageChannel)
            pusher.disconnect()
        }
    },[])

    return (
        <>
            <div className="wrapper">
                <div className="search-sidebar">
                    <SearchBar setDeviceCheck={setDeviceCheck} deviceCheck={deviceCheck}/>
                </div>
                <div className="status-wrapper">
                    <Status setDeviceCheck={setDeviceCheck} deviceCheck={deviceCheck}/>
                </div>
            </div>
        </>
    )
}
