import React,{useEffect,useState} from 'react'
import {io} from 'socket.io-client'
import infinityScroll from './InfinityScroll'

export default function SidebarBox({setClickPhoto}) {
    const [location,setLocation] = useState()
    const [photo,setPhoto] = useState()
    const [socket,setSocket] = useState()
    const [pageNumber,setPageNumber] = useState(4)
    const {
        inventory,
        hasMore,
        error,
        loading
    } = infinityScroll(pageNumber)

    useEffect(() => {
        const s = io(`http://localhost:5000`)
        setSocket(s)
        return () =>{
            s.disconnect()  
        }
    }, [])  

    useEffect(() =>{
        if (socket == null) return
        const handler = (delta) =>{
            setLocation(delta['location'])
            setPhoto(delta['photo'])
        }
        socket.on('display-result',handler)
        return () =>{
            socket.off('display-result',handler)
        }
    },[socket,photo,location])


    const photoOnClick = (() => {
        setClickPhoto(photo)
    })

    return (
        <div className="grid-item sidebar-wrapper">
            {inventory.map(item=>{
                return <div className="inventory-item">
                    <div key={item.location}>Location: {item.location}</div>
                    <img key={item.photo} src={item.photo} alt="sideBarPhoto" onClick={photoOnClick} className="img-sideBar"/>
                </div>
            })}
            <div>{loading && 'Loading...'}</div>
            <div>{error && 'Loading...'}</div>
            </div>
    )
}
