import React,{useRef,useState,useEffect} from 'react'

export default function Mapping() {

      const canvas = useRef()
      const img = useRef()
      const [context,setContext] = useState()
      const [image,setImage] = useState()
    
      useEffect(() => {
        setContext(canvas.current.getContext("2d"))
        setImage()
        // context.save()
        // context.scale(pixelRatio, pixelRatio)
        // context.fillStyle = "hsl(0, 0%, 95%)"
        // context.fillRect(0, 0, width, height)
    
        // context.strokeStyle = "black"
        // context.beginPath()
        // context.arc(width / 2, height / 2, width / 4, 0, Math.PI * 2)
        // context.stroke()
        // context.restore()
      },[])
    
    //   const dw = Math.floor(pixelRatio * width)
    //   const dh = Math.floor(pixelRatio * height)
    //   const style = { width, height }

    const handleOnLoad = () => {
        context.drawImage(img, 0, 0)
        context.font = "40px Courier"
        context.fillText("mapping", 210, 75)
    }

    return (
        <div className="mapping">
            <canvas ref={canvas} width={600} height={425} />
            <img ref={img} src="mapping.png" alt="mapping" className="mapping-img" onLoad={handleOnLoad}/>
        </div>        
        // bg-light        
        // style={onClick ? {height: 300}: {height: 455}}
    )
}
